import pandas as pdimport osdef init_data():    data_full = None    for file in os.listdir():        if ".csv" in file:            data_full = pd.read_csv(file, sep=";")            break    data_full = data_full.drop(columns=['Tag', 'Rekening', 'Code'])    data_full["(5)Parent Category"] = data_full["(6)Category"] = "All"    data_full["(11) Status"] = data_full["(12) Split"] = ""    data_full["(8)Account"] = "ING"    data_full = data_full.rename(        columns={"Af Bij": "(1)Type", "Datum": "(2)Date", "Naam / Omschrijving": "(3)Item or Payee",                 "Bedrag (EUR)": "(4)Amount", "Mutatiesoort": "(7)Account Type", "Mededelingen": "(9)Notes",                 "Tegenrekening": "(10) Label"}, errors="raise")    data = data_full.drop(columns={"Saldo na mutatie"})    return data[['(1)Type', '(2)Date', '(3)Item or Payee', '(4)Amount', '(5)Parent Category', '(6)Category',                 '(7)Account Type', '(8)Account', '(9)Notes', '(10) Label', '(11) Status', '(12) Split']]def clean_type(data):    data["(1)Type"] = data["(1)Type"].replace("Af", 'e')    data["(1)Type"] = data["(1)Type"].replace("Bij", 'i')def clean_date(data):    data["(2)Date"] = data["(2)Date"].astype(str).apply(        lambda date: str(int(date[4:6])) + '-' + str(int(date[6:8])) + '-' + str(int(date[0:4])))def clean_amount(data):    data["(4)Amount"] = data["(4)Amount"].apply(lambda a: a.replace(',', '.')).astype(float)def clean(data):    clean_type(data)    clean_date(data)    clean_amount(data)def main():    data = init_data()    clean(data)    data.to_csv("output.csv", index=False)main()